import { AppBar, Box, Button, Container, FormControl, InputLabel, Link, Paper, Select, TextField, Toolbar, Typography } from '@mui/material'
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';
import IRestaurante from '../../interfaces/IRestaurante';
import http from '../../http';
import { Link as RouterLink } from 'react-router-dom';
import ITags from '../../interfaces/ITags';

export default function FormularioPratos() {
    const [nomePrato, setNomePrato] = useState('')
    const [descricao, setDescricao] = useState('')
    const [tags, setTags] = useState<ITags[]>([])
    // const parametros = useParams();

    useEffect(() => {
        http.get<{tags: ITags[]}>(`tags/`)
            .then(res => {
                setTags(res.data.tags)
            })
    }, [])

    const onSubmit = (event: React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();
    }

  return (
    <>
        <Box sx={{display:"flex", flexDirection:"column", alignItems:"center", flexGrow: 1}}>
            <Typography component="h1" variant="h6">Formul√°rio Pratos</Typography>
            <Box component="form" sx={{ width: '100%' }} onSubmit={onSubmit}>
                <TextField 
                    value={nomePrato}
                    id="standard-basic" 
                    label="Nome do Prato" 
                    variant="standard" 
                    onChange={event => setNomePrato(event.target.value)}
                    fullWidth
                    required
                    margin="dense"
                />
                <TextField 
                    value={descricao}
                    id="standard-basic" 
                    label="Descricao do Prato" 
                    variant="standard" 
                    onChange={event => setDescricao(event.target.value)}
                    fullWidth
                    required
                    margin="dense"
                />
                <FormControl margin='dense' fullWidth >
                    <InputLabel id='select-tag'>Tag</InputLabel>

                </FormControl>
                <Button 
                    type="submit"
                    variant="outlined"
                    fullWidth
                    sx={{marginTop: 1}}>
                    Salvar
                </Button>
            </Box>
        </Box>
    </>
  )
}
